$html = Invoke-RestMethod -Uri $url -Method Get -UseBasicParsing
try {
    $updates = $html.html.body.div.ul.li.a
    $latest = $updates[0].'#text'.replace(' Update','')
    return $latest
} catch {
    return $null
}

